import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import {
  Container,
  Grid,
  Typography,
  Box,
  CircularProgress,
  Pagination,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  FormControlLabel,
  Switch,
  Paper,
  Divider,
  Alert,
  Button,
  IconButton,
  Tooltip,
  Link
} from '@mui/material';
import {
  FilterAlt as FilterIcon,
  Clear as ClearIcon,
  Refresh as RefreshIcon,
  Archive as ArchiveIcon
} from '@mui/icons-material';
import ProjectCard from '../components/ProjectCard';
import { getProjects, markProjectsAsSeen } from '../services/api';

function HomePage() {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);

  // State for projects and pagination
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(parseInt(queryParams.get('page')) || 1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);

  // State for filters
  const [searchQuery, setSearchQuery] = useState(queryParams.get('search') || '');
  const [location_, setLocation] = useState(queryParams.get('location') || '');
  const [remoteOnly, setRemoteOnly] = useState(queryParams.get('remote') === 'true');
  const [newOnly, setNewOnly] = useState(queryParams.get('new') === 'true');
  const [showAllProjects, setShowAllProjects] = useState(true);
  const [showFilters, setShowFilters] = useState(false);

  // State for new projects
  const [newProjectIds, setNewProjectIds] = useState([]);

  // Load projects on mount and when filters change
  useEffect(() => {
    const fetchProjects = async () => {
      setLoading(true);
      setError(null);

      try {
        // Build query parameters
        const params = {
          page,
          limit: 12,
          search: searchQuery,
          location: location_,
          remote: remoteOnly,
          new: newOnly,
          show_all: showAllProjects
        };

        console.log('Fetching projects with params:', params);
        const data = await getProjects(params);
        console.log('API response:', data);
        console.log('Projects received:', data.projects ? data.projects.length : 0);

        // Das Backend gibt ein Objekt mit einem Feld 'projects' zurück, nicht 'data'
        setProjects(data.projects || []);
        // Berechne totalPages basierend auf total und limit
        const calculatedTotalPages = Math.ceil(data.total / params.limit);
        setTotalPages(calculatedTotalPages || 1);
        setTotalCount(data.total || 0);

        // Set new project IDs if available
        if (data.newProjectIds) {
          setNewProjectIds(data.newProjectIds);
        }
      } catch (err) {
        console.error('Error fetching projects:', err);
        setError('Fehler beim Laden der Projekte. Bitte versuchen Sie es später erneut.');
        setProjects([]);
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, [page, searchQuery, location_, remoteOnly, showAllProjects]);

  // Update URL when filters change
  useEffect(() => {
    const params = new URLSearchParams();
    if (page > 1) params.set('page', page.toString());
    if (searchQuery) params.set('search', searchQuery);
    if (location_) params.set('location', location_);
    if (remoteOnly) params.set('remote', 'true');
    if (newOnly) params.set('new', 'true');
    if (showAllProjects) params.set('showAll', 'true');

    navigate({ search: params.toString() }, { replace: true });
  }, [page, searchQuery, location_, remoteOnly, newOnly, showAllProjects, navigate]);

  const handlePageChange = (event, value) => {
    setPage(value);
    window.scrollTo(0, 0);
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    // Reset to page 1 when filters change
    setPage(1);
  };

  const handleClearFilters = () => {
    setSearchQuery('');
    setLocation('');
    setRemoteOnly(false);
    setNewOnly(false);
    setPage(1);
  };

  const handleFavoriteToggle = () => {
    // This is just to force a re-render when a favorite is toggled
    setProjects([...projects]);
  };

  const handleMarkAsSeen = async (projectIds) => {
    try {
      await markProjectsAsSeen(projectIds);
      // Remove the marked projects from the newProjectIds list
      setNewProjectIds(newProjectIds.filter(id => !projectIds.includes(id)));
    } catch (error) {
      console.error('Error marking projects as seen:', error);
    }
  };

  return (
    <Container maxWidth="xl">
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          {showAllProjects ? 'GULP Freiberufler-Projekte (Alle)' : 'GULP Freiberufler-Projekte (Letzte 24 Stunden)'}
        </Typography>
        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          {showAllProjects 
            ? 'Durchsuchen Sie alle verfügbaren Projekte für Freiberufler auf GULP.de' 
            : 'Durchsuchen Sie die neuesten Projekte der letzten 24 Stunden für Freiberufler auf GULP.de'
          }
        </Typography>
        <Button 
          component={Link} 
          href="/scraper?tab=3" 
          startIcon={<ArchiveIcon />} 
          variant="outlined" 
          size="small" 
          sx={{ mt: 1 }}
        >
          Ältere Projekte im Archiv ansehen
        </Button>
      </Box>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6" component="h2">
            Filter
          </Typography>
          <IconButton 
            size="small" 
            onClick={() => setShowFilters(!showFilters)}
            sx={{ ml: 1 }}
          >
            <FilterIcon />
          </IconButton>
          <Box sx={{ flexGrow: 1 }} />
          <Tooltip title="Filter zurücksetzen">
            <IconButton 
              size="small" 
              onClick={handleClearFilters}
              disabled={!searchQuery && !location_ && !remoteOnly}
            >
              <ClearIcon />
            </IconButton>
          </Tooltip>
        </Box>

        {showFilters && (
          <form onSubmit={handleFilterSubmit}>
            <Grid container spacing={2} alignItems="center">
              <Grid item xs={12} sm={6} md={4}>
                <TextField
                  fullWidth
                  label="Suchbegriff"
                  variant="outlined"
                  size="small"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Titel, Beschreibung, Firma..."
                />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <TextField
                  fullWidth
                  label="Ort"
                  variant="outlined"
                  size="small"
                  value={location_}
                  onChange={(e) => setLocation(e.target.value)}
                  placeholder="z.B. München, Berlin..."
                />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={remoteOnly}
                      onChange={(e) => setRemoteOnly(e.target.checked)}
                      color="primary"
                    />
                  }
                  label="Nur Remote-Projekte"
                />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={newOnly}
                      onChange={(e) => setNewOnly(e.target.checked)}
                      color="primary"
                    />
                  }
                  label="Nur neue Projekte"
                />
              </Grid>
              <Grid item xs={12} sm={6} md={4}>
                <FormControlLabel
                  control={
                    <Switch
                      checked={showAllProjects}
                      onChange={(e) => setShowAllProjects(e.target.checked)}
                      color="primary"
                    />
                  }
                  label="Alle Projekte anzeigen (inkl. Archiv)"
                />
              </Grid>
            </Grid>
          </form>
        )}
      </Paper>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
          <Button 
            startIcon={<RefreshIcon />}
            onClick={() => window.location.reload()}
            sx={{ ml: 2 }}
          >
            Neu laden
          </Button>
        </Alert>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="subtitle1">
          {loading ? 'Lade Projekte...' : `${totalCount} Projekte gefunden`}
        </Typography>
      </Box>

      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
          <CircularProgress />
        </Box>
      ) : projects.length > 0 ? (
        <>
          <Grid container spacing={3}>
            {projects.map((project) => (
              <Grid item xs={12} sm={6} md={4} lg={3} key={project.id}>
                <ProjectCard 
                  project={project} 
                  onFavoriteToggle={handleFavoriteToggle}
                  isNew={newProjectIds.includes(project.id)}
                  onMarkAsSeen={() => handleMarkAsSeen([project.id])}
                />
              </Grid>
            ))}
          </Grid>

          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <Pagination 
              count={totalPages} 
              page={page} 
              onChange={handlePageChange} 
              color="primary" 
              size="large"
              showFirstButton
              showLastButton
            />
          </Box>
        </>
      ) : (
        <Box sx={{ textAlign: 'center', my: 4 }}>
          <Typography variant="h6" color="text.secondary">
            Keine Projekte gefunden
          </Typography>
          <Typography variant="body2" color="text.secondary">
            Versuchen Sie es mit anderen Filterkriterien
          </Typography>
          <Button 
            variant="outlined" 
            color="primary" 
            onClick={handleClearFilters}
            sx={{ mt: 2 }}
          >
            Filter zurücksetzen
          </Button>
        </Box>
      )}
    </Container>
  );
}

export default HomePage;
