import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  CircularProgress,
  Alert,
  Divider,
  FormControlLabel,
  Switch,
  Grid,
  TextField,
  InputAdornment,
  IconButton,
  Card,
  CardContent,
  Stack
} from '@mui/material';
import AccessTimeIcon from '@mui/icons-material/AccessTime';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import AddIcon from '@mui/icons-material/Add';
import DeleteIcon from '@mui/icons-material/Delete';
import { getSchedulerConfig, setSchedulerConfig } from '../services/api';

const SchedulerConfigForm = () => {
  const [config, setConfig] = useState({
    enabled: true,
    interval_days: 1,
    daily_runs: [
      { hour: 3, minute: 0 }
    ]
  });

  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchConfig();
  }, []);

  const fetchConfig = async () => {
    try {
      setLoading(true);
      const data = await getSchedulerConfig();
      setConfig(data);
      setError(null);
    } catch (err) {
      console.error('Fehler beim Laden der Scheduler-Konfiguration:', err);
      setError('Die Scheduler-Konfiguration konnte nicht geladen werden.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setSaving(true);
      setError(null);
      setSuccess(null);

      await setSchedulerConfig(config);

      setSuccess('Scheduler-Konfiguration wurde erfolgreich gespeichert!');

      // Erfolgsmeldung nach 5 Sekunden ausblenden
      setTimeout(() => setSuccess(null), 5000);
    } catch (err) {
      console.error('Fehler beim Speichern der Scheduler-Konfiguration:', err);

      if (err.response && err.response.data && err.response.data.error) {
        setError(err.response.data.error);
      } else {
        setError('Die Scheduler-Konfiguration konnte nicht gespeichert werden.');
      }
    } finally {
      setSaving(false);
    }
  };

  const handleChange = (field) => (event) => {
    const value = field === 'enabled' ? event.target.checked : 
                  field === 'interval_days' ? parseInt(event.target.value, 10) : 
                  event.target.value;

    setConfig({
      ...config,
      [field]: value
    });
  };

  const handleRunChange = (index, field) => (event) => {
    const value = parseInt(event.target.value, 10);
    const updatedRuns = [...config.daily_runs];
    updatedRuns[index] = { ...updatedRuns[index], [field]: value };

    setConfig({
      ...config,
      daily_runs: updatedRuns
    });
  };

  const addRun = () => {
    // Default to noon if there's already a morning run
    const newHour = config.daily_runs.length > 0 ? 12 : 3;
    setConfig({
      ...config,
      daily_runs: [...config.daily_runs, { hour: newHour, minute: 0 }]
    });
  };

  const removeRun = (index) => {
    if (config.daily_runs.length <= 1) {
      setError('Mindestens ein Ausführungszeitpunkt muss konfiguriert sein.');
      return;
    }

    const updatedRuns = config.daily_runs.filter((_, i) => i !== index);
    setConfig({
      ...config,
      daily_runs: updatedRuns
    });
  };

  const formatTime = (hour, minute) => {
    return `${hour.toString().padStart(2, '0')}:${minute.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Paper sx={{ p: 3, mb: 4 }}>
      <Typography variant="h6" gutterBottom>
        Scheduler-Konfiguration
      </Typography>

      <Typography variant="body2" color="text.secondary" paragraph>
        Konfigurieren Sie, wann und wie oft der Scraper automatisch ausgeführt werden soll.
      </Typography>

      <Divider sx={{ my: 2 }} />

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {success && (
        <Alert severity="success" sx={{ mb: 2 }}>
          {success}
        </Alert>
      )}

      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <FormControlLabel
              control={
                <Switch
                  checked={config.enabled}
                  onChange={handleChange('enabled')}
                  color="primary"
                />
              }
              label="Automatische Ausführung aktivieren"
            />
          </Grid>

          <Grid item xs={12}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="subtitle2">
                Ausführungszeiten pro Tag
              </Typography>
              <Button 
                startIcon={<AddIcon />} 
                variant="outlined" 
                size="small" 
                onClick={addRun}
                disabled={!config.enabled || config.daily_runs.length >= 5}
              >
                Hinzufügen
              </Button>
            </Box>

            {config.daily_runs.map((run, index) => (
              <Card key={index} variant="outlined" sx={{ mb: 2 }}>
                <CardContent>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 1 }}>
                    <Typography variant="subtitle2">
                      Ausführung {index + 1}
                    </Typography>
                    <IconButton 
                      size="small" 
                      color="error" 
                      onClick={() => removeRun(index)}
                      disabled={!config.enabled || config.daily_runs.length <= 1}
                    >
                      <DeleteIcon fontSize="small" />
                    </IconButton>
                  </Box>

                  <Stack direction="row" spacing={2} alignItems="center">
                    <TextField
                      label="Stunde"
                      type="number"
                      value={run.hour}
                      onChange={handleRunChange(index, 'hour')}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <AccessTimeIcon fontSize="small" />
                          </InputAdornment>
                        ),
                      }}
                      inputProps={{
                        min: 0,
                        max: 23,
                        step: 1
                      }}
                      disabled={!config.enabled}
                      sx={{ width: '45%' }}
                    />
                    <Typography variant="body1">:</Typography>
                    <TextField
                      label="Minute"
                      type="number"
                      value={run.minute}
                      onChange={handleRunChange(index, 'minute')}
                      inputProps={{
                        min: 0,
                        max: 59,
                        step: 1
                      }}
                      disabled={!config.enabled}
                      sx={{ width: '45%' }}
                    />
                  </Stack>
                </CardContent>
              </Card>
            ))}

            <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
              {config.daily_runs.length === 1 
                ? `Der Scraper wird täglich um ${formatTime(config.daily_runs[0].hour, config.daily_runs[0].minute)} Uhr ausgeführt.`
                : `Der Scraper wird täglich zu ${config.daily_runs.length} verschiedenen Zeiten ausgeführt.`}
            </Typography>
          </Grid>

          <Grid item xs={12} sm={6}>
            <Typography variant="subtitle2" gutterBottom>
              Ausführungsintervall (Tage)
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <CalendarMonthIcon sx={{ mr: 1, color: 'text.secondary' }} />
              <TextField
                type="number"
                fullWidth
                value={config.interval_days}
                onChange={handleChange('interval_days')}
                inputProps={{
                  min: 1,
                  max: 30,
                  step: 1
                }}
                disabled={!config.enabled}
              />
            </Box>
            <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
              {config.interval_days === 1 
                ? 'Der Scraper wird täglich ausgeführt.' 
                : `Der Scraper wird alle ${config.interval_days} Tage ausgeführt.`}
            </Typography>
          </Grid>

          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              disabled={saving}
              startIcon={saving ? <CircularProgress size={20} color="inherit" /> : null}
            >
              {saving ? 'Wird gespeichert...' : 'Konfiguration speichern'}
            </Button>
          </Grid>
        </Grid>
      </form>
    </Paper>
  );
};

export default SchedulerConfigForm;
